                                            } catch (_: Exception) { }
                                            // enviar verificacion de forma asíncrona y esperar
                                            try {
                                                res.user?.sendEmailVerification()?.await()
                                            } catch (_: Exception) { }
                                            // Asegurarse de no dejar al importer autenticado
                                            try { authImporter.signOut() } catch (_: Exception) {}
                                        } else {
                                            // Guardar doc y encolar petición para backend
                                            val data = hashMapOf(
                                                "nombres" to nombre,
                                                "apellidos" to apellidos,
                                                "email" to email,
                                                "tipo" to tipo,
                                                "role" to roleParsed,
                                                "importedAt" to Timestamp.now()
                                            )
                                            db.collection(coll).add(data).await()
                                            db.collection("auth_queue").add(hashMapOf(
                                                "email" to email,
                                                "role" to roleParsed,
                                                "displayName" to ("$nombre $apellidos").trim(),
                                                "requestedAt" to Timestamp.now()
                                            )).await()

                                            // Cambio mínimo: crear también un documento en "users" para que la app tenga acceso al correo
                                            try {
                                                val userMap = hashMapOf(
                                                    "displayName" to ("$nombre $apellidos").trim(),
                                                    "email" to email,
                                                    "role" to roleParsed,
                                                    "importedAt" to Timestamp.now()
                                                )
                                                db.collection("users").add(userMap).await()
                                            } catch (_: Exception) { }

                                            // Intentar enviar email de restablecimiento (si la cuenta ya existe esto ayudará al usuario a establecer contraseña)
                                            try {
                                                val mainAuth = FirebaseAuth.getInstance()
                                                mainAuth.sendPasswordResetEmail(email).await()
                                            } catch (_: Exception) { }
                                        }
                                        count++
                                    } catch (e: Exception) {
                                        // registrar error y continuar
                                    }
                                }
                                wb.close()
                            }
                        }
                    } else {
                        // CSV: usar lector robusto (maneja comillas, BOM y codificación UTF-8/CP1252)
                        context.contentResolver.openInputStream(uri)?.use { input ->
                            // detectar BOM / probar UTF-8 primero
                            val possibleCharsets = listOf(Charset.forName("UTF-8"), Charset.forName("windows-1252"))
                            var reader: BufferedReader? = null
                            for (cs in possibleCharsets) {
                                try {
                                    reader = BufferedReader(InputStreamReader(input, cs))
                                    reader.mark(4096)
                                    // try to read first line and reset; if decode okay, break
                                    reader.readLine()
                                    reader.reset()
                                    break
                                } catch (_: Exception) {
                                    try { reader?.close() } catch (_: Exception) {}
                                    reader = null
                                    try { input.reset() } catch (_: Exception) {}
                                }
                            }
                            if (reader == null) reader = BufferedReader(InputStreamReader(input, Charset.forName("UTF-8")))

                            // función auxiliar para parsear línea CSV que respeta comillas
                            fun parseCsvLine(line: String): List<String> {
                                val regex = ",(?=(?:[^\"]*\"[^\"]*\")*[^\"]*$)".toRegex()
                                return line.split(regex).map { it.trim().trim('"').let { s -> Normalizer.normalize(s, Normalizer.Form.NFC) } }
                            }

                            // descartar header si existe
                            var line = reader.readLine() ?: ""
                            // heurística: si primera línea contiene "name" o "email" considerarla header
                            if (line.contains("email", true) || line.contains("nombre", true) || line.contains("name", true)) {
                                // ya descartada
                            } else {
                                // procesar esta primera línea también
                                val parts0 = parseCsvLine(line)
                                if (parts0.size >= 3) {
                                    val type = parts0[0].lowercase().trim()
                                    val email = parts0.getOrNull(1)?.trim() ?: ""
                                    val nameVal = parts0.getOrNull(2)?.trim() ?: ""
                                    val parsedRole = when (parts0.getOrNull(3)?.trim()?.uppercase()) {
                                        "PADRE" -> "PADRE"
                                        "DOCENTE" -> "DOCENTE"
                                        "ADMIN" -> "ADMIN"
                                        else -> "ESTUDIANTE"
                                    }
                                    val maybePass = parts0.getOrNull(4)?.trim()?.takeIf { it.isNotBlank() }
                                    val coll = when (parsedRole) {
                                        "PADRE" -> "parents"
                                        "DOCENTE" -> "teachers"
                                        "ADMIN" -> "admins"
                                        else -> "students"
                                    }
                                    try {
                                        val display = Normalizer.normalize(nameVal, Normalizer.Form.NFC)
                                        if (maybePass != null && authImporter != null) {
                                            val res = authImporter.createUserWithEmailAndPassword(email, maybePass).await()
                                            val uid = res.user?.uid ?: db.collection(coll).document().id
                                            db.collection(coll).document(uid).set(hashMapOf("name" to display, "email" to email, "role" to parsedRole, "type" to type, "importedAt" to Timestamp.now())).await()
                                        } else {
                                            db.collection(coll).add(hashMapOf("email" to email, "name" to display, "role" to parsedRole, "type" to type, "importedAt" to Timestamp.now())).await()
                                            db.collection("auth_queue").add(hashMapOf("email" to email, "role" to parsedRole, "displayName" to display, "requestedAt" to Timestamp.now())).await()
                                        }
                                        count++
                                    } catch (_: Exception) {}
                                }
                            }

                            var localCount = 0
                            while (true) {
                                line = reader.readLine() ?: break
                                val parts = parseCsvLine(line)
                                if (parts.size >= 3) {
                                    val type = parts[0].lowercase().trim()
                                    val email = parts.getOrNull(1)?.trim() ?: ""
                                    val nameVal = parts.getOrNull(2)?.trim() ?: ""
                                    val parsedRole = when (parts.getOrNull(3)?.trim()?.uppercase()) {
                                        "PADRE" -> "PADRE"
                                        "DOCENTE" -> "DOCENTE"
                                        "ADMIN" -> "ADMIN"
                                        else -> "ESTUDIANTE"
                                    }
                                    val maybePass = parts.getOrNull(4)?.trim()?.takeIf { it.isNotBlank() }
                                    val coll = when (parsedRole) {
                                        "PADRE" -> "parents"
                                        "DOCENTE" -> "teachers"
                                        "ADMIN" -> "admins"
                                        else -> "students"
                                    }

                                    try {
                                        val display = Normalizer.normalize(nameVal, Normalizer.Form.NFC)
                                        if (maybePass != null && authImporter != null) {
                                            val res = authImporter.createUserWithEmailAndPassword(email, maybePass).await()
                                            val uid = res.user?.uid ?: db.collection(coll).document().id
                                            db.collection(coll).document(uid).set(hashMapOf(
                                                "name" to display,
                                                "email" to email,
                                                "role" to parsedRole,
                                                "type" to type,
                                                "importedAt" to Timestamp.now()
                                            )).await()
                                        } else {
                                            db.collection(coll).add(hashMapOf(
                                                "email" to email,
                                                "name" to display,
                                                "role" to parsedRole,
                                                "type" to type,
                                                "importedAt" to Timestamp.now()
                                            )).await()
                                            db.collection("auth_queue").add(hashMapOf(
                                                "email" to email,
                                                "role" to parsedRole,
                                                "displayName" to display,
                                                "requestedAt" to Timestamp.now()
                                            )).await()
                                        }
                                        localCount++
                                    } catch (e: Exception) {
                                        // continuar
                                    }
                                }
                            }
                            count += localCount
                        }
                    }

                    status = "Se importaron $count registros correctamente."
                } catch (e: Exception) {
                    status = "Error importando: ${e.message}"
                } finally {
                    isLoading = false
                }
            }
        }
    }

    Column(
        modifier = Modifier
            .fillMaxSize()
            .padding(16.dp),
        verticalArrangement = Arrangement.spacedBy(16.dp)
    ) {
        Text(
            "Panel de Administración",
            style = MaterialTheme.typography.headlineMedium,
            fontWeight = FontWeight.Bold
        )

        // Tarjetas principales: Registrar usuario, Ver perfiles, Gestionar horarios, Crear evento/Notificación
        Row(Modifier.fillMaxWidth(), horizontalArrangement = Arrangement.spacedBy(12.dp)) {
            ElevatedCard(modifier = Modifier.weight(1f), onClick = {
                val intent = android.content.Intent(context, RegisterActivity::class.java)
                intent.putExtra("fromAdmin", true)
                context.startActivity(intent)
            }) {
                Box(Modifier.height(84.dp).fillMaxWidth(), contentAlignment = Alignment.Center) { Text("Registrar usuario") }
            }

            ElevatedCard(modifier = Modifier.weight(1f), onClick = {
                navController?.navigate(com.example.appcolegios.navigation.AppRoutes.AdminUsers.route)
            }) {
                Box(Modifier.height(84.dp).fillMaxWidth(), contentAlignment = Alignment.Center) { Text("Ver perfiles") }
            }

            ElevatedCard(modifier = Modifier.weight(1f), onClick = {
                // Ir a la selección de usuarios (luego seleccionar para gestionar horario)
                navController?.navigate(com.example.appcolegios.navigation.AppRoutes.AdminUsers.route)
            }) {
                Box(Modifier.height(84.dp).fillMaxWidth(), contentAlignment = Alignment.Center) { Text("Gestionar horarios") }
            }

            ElevatedCard(modifier = Modifier.weight(1f), onClick = {
                navController?.navigate(com.example.appcolegios.navigation.AppRoutes.AdminEventCreate.route)
            }) {
                Box(Modifier.height(84.dp).fillMaxWidth(), contentAlignment = Alignment.Center) { Text("Crear evento/Notificación") }
            }
        }

        if (status != null) {
            Card(
                colors = CardDefaults.cardColors(
                    containerColor = if (status!!.contains("Error"))
                        MaterialTheme.colorScheme.errorContainer
                    else
                        MaterialTheme.colorScheme.primaryContainer
                )
            ) {
                Text(
                    status!!,
                    modifier = Modifier.padding(16.dp),
                    color = if (status!!.contains("Error"))
                        MaterialTheme.colorScheme.onErrorContainer
                    else
                        MaterialTheme.colorScheme.onPrimaryContainer
                )
            }
        }

        if (isLoading) {
            CircularProgressIndicator()
        }

        // Botón para inicializar datos de prueba
        Card(
            colors = CardDefaults.cardColors(
                containerColor = MaterialTheme.colorScheme.surface
            ),
            elevation = CardDefaults.cardElevation(defaultElevation = 2.dp)
        ) {
            Column(Modifier.padding(16.dp)) {
                Text(
                    "Inicializar Datos de Prueba",
                    style = MaterialTheme.typography.titleMedium,
                    fontWeight = FontWeight.Bold
                )
                Spacer(Modifier.height(8.dp))
                Text(
                    "Crea usuarios de prueba con información completa:\n" +
                    "• Estudiante: jcamilodiaz7@gmail.com\n" +
                    "• Profesor: hermanitos605@gmail.com\n" +
                    "• Admin: jcamilodiaz777@gmail.com\n" +
                    "• Padre: hermanitos604@gmail.com",
                    style = MaterialTheme.typography.bodyMedium
                )
                Spacer(Modifier.height(12.dp))
                Button(
                    onClick = {
                        scope.launch {
                            isLoading = true
                            status = null
                            try {
                                TestDataInitializer.initializeAllTestData()
                                status = "✅ Datos de prueba inicializados correctamente"
                            } catch (e: Exception) {
                                status = "❌ Error: ${e.message}"
                            } finally {
                                isLoading = false
                            }
                        }
                    },
                    enabled = !isLoading,
                    modifier = Modifier.fillMaxWidth()
                ) {
                    Text("Inicializar Datos de Prueba")
                }
            }
        }

        // Botón para importar desde CSV/XLSX
        Button(
            onClick = { filePicker.launch(arrayOf("application/vnd.openxmlformats-officedocument.spreadsheetml.sheet","text/*","text/csv","text/comma-separated-values")) },
            enabled = !isLoading,
            modifier = Modifier.fillMaxWidth()
        ) {
            Text("Importar desde CSV / Excel (.xlsx)")
        }

        Text(
            "Formato CSV: tipo,email,nombre,rol\nEjemplo: estudiante,juan@mail.com,Juan Pérez,ESTUDIANTE",
            style = MaterialTheme.typography.bodySmall,
            color = MaterialTheme.colorScheme.onSurfaceVariant
        )
    }
}

package com.example.appcolegios.admin

import androidx.navigation.NavController
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.MoreVert
import androidx.compose.material.icons.filled.Notifications
import androidx.compose.material.icons.filled.Event
import androidx.compose.material.icons.filled.Schedule
import com.google.firebase.firestore.FirebaseFirestore
import com.google.firebase.Timestamp
import androidx.compose.foundation.clickable
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.lazy.items
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Modifier
import androidx.compose.ui.unit.dp
import kotlinx.coroutines.tasks.await
import androidx.compose.ui.Alignment
import androidx.compose.material3.IconButton
import androidx.compose.material3.TextButton
import androidx.compose.material3.AlertDialog
import androidx.compose.material3.Icon
import java.util.*

@Composable
fun AdminUsersScreen(navController: NavController? = null, onUserSelected: (String) -> Unit = {}) {
    val db = FirebaseFirestore.getInstance()
    var users by remember { mutableStateOf<List<Map<String, Any>>>(emptyList()) }
    var isLoading by remember { mutableStateOf(true) }
    var error by remember { mutableStateOf<String?>(null) }

    LaunchedEffect(Unit) {
        isLoading = true
        try {
            // Preferir listar estudiantes (colección "students") porque la gestión de horarios aplica a estudiantes.
            val snap = db.collection("students").get().await()
            val list = mutableListOf<Map<String, Any>>()
            if (!snap.isEmpty) {
                for (doc in snap.documents) {
                    val map = doc.data?.toMutableMap() ?: mutableMapOf()
                    // Normalizar campos: id, displayName, email, role
                    map["id"] = doc.id
                    val nombre = (map["nombre"] as? String) ?: (map["name"] as? String) ?: "(sin nombre)"
                    map["displayName"] = nombre
                    map["email"] = map["email"] ?: ""
                    map["role"] = "ESTUDIANTE"
                    list.add(map)
                }
            } else {
                // Fallback: listar colección "users" si no hay estudiantes
                val us = db.collection("users").get().await()
                for (doc in us.documents) {
                    val map = doc.data?.toMutableMap() ?: mutableMapOf()
                    map["id"] = doc.id
                    list.add(map)
                }
            }
            users = list
        } catch (e: Exception) {
            error = e.message
        } finally {
            isLoading = false
        }
    }

    if (isLoading) {
        Box(Modifier.fillMaxSize(), contentAlignment = Alignment.Center) { CircularProgressIndicator() }
        return
    }
    if (error != null) {
        Box(Modifier.fillMaxSize(), contentAlignment = Alignment.Center) { Text("Error: $error") }
        return
    }

    Card(Modifier.fillMaxSize().padding(8.dp)) {
        LazyColumn(contentPadding = PaddingValues(8.dp)) {
            items(users) { u ->
                val id = u["id"] as? String ?: ""
                val name = (u["displayName"] as? String) ?: (u["name"] as? String) ?: "(sin nombre)"
                val email = (u["email"] as? String) ?: ""
                val role = (u["role"] as? String) ?: ""
                var actionsOpen by remember { mutableStateOf(false) }
                Column(modifier = Modifier
                    .fillMaxWidth()
                    .padding(vertical = 8.dp)
                ) {
                    Row(modifier = Modifier.fillMaxWidth(), verticalAlignment = Alignment.CenterVertically) {
                        Column(modifier = Modifier.weight(1f).clickable { onUserSelected(id) }) {
                            Text(name, style = MaterialTheme.typography.titleMedium)
                            Spacer(Modifier.height(4.dp))
                            Text(if (email.isNotBlank()) "$email · ${role}" else role, style = MaterialTheme.typography.bodySmall)
                        }
                        IconButton(onClick = { actionsOpen = true }) { Icon(Icons.Filled.MoreVert, contentDescription = "Acciones") }
                    }
                    HorizontalDivider(color = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.08f))
                    Spacer(Modifier.height(4.dp))

                    if (actionsOpen) {
                        // Dialog with user details and actions
                        AlertDialog(onDismissRequest = { actionsOpen = false }, title = { Text(name) }, text = {
                            Column {
                                Text("Email: $email")
                                Spacer(Modifier.height(8.dp))
                                Text("Rol: $role")
                            }
                        }, confirmButton = {
                            Row {
                                TextButton(onClick = {
                                    actionsOpen = false
                                    // navigate to schedule manager for this user
                                    navController?.navigate(com.example.appcolegios.navigation.AppRoutes.AdminScheduleManage.route.replace("{userId}", id))
                                }) { Icon(Icons.Filled.Schedule, contentDescription = null); Spacer(Modifier.width(6.dp)); Text("Agregar horario") }
                                Spacer(Modifier.width(8.dp))
                                TextButton(onClick = {
                                    actionsOpen = false
                                    // open simple notification dialog
                                    sendNotificationForUser(db, id, name)
                                }) { Icon(Icons.Filled.Notifications, contentDescription = null); Spacer(Modifier.width(6.dp)); Text("Enviar notificación") }
                                Spacer(Modifier.width(8.dp))
                                TextButton(onClick = {
                                    actionsOpen = false
                                    // open event dialog to add event to user's calendar
                                    addEventForUser(db, id, name)
                                }) { Icon(Icons.Filled.Event, contentDescription = null); Spacer(Modifier.width(6.dp)); Text("Agregar evento") }
                            }
                        }, dismissButton = { TextButton(onClick = { actionsOpen = false }) { Text("Cerrar") } })
                    }
                }
            }
        }
    }
}

// Helper: open a small UI to send notification; implemented as immediate write after simple default content for speed
private fun sendNotificationForUser(db: FirebaseFirestore, userId: String, senderName: String) {
    // For simplicity create a generic notification; in UI we could ask title/body — keeping it minimal
    try {
        val notif = hashMapOf(
            "titulo" to "Mensaje del Admin",
            "cuerpo" to "Has recibido una notificación del administrador.",
            "remitente" to "admin",
            "senderName" to senderName,
            "fechaHora" to Timestamp.now(),
            "leida" to false,
            "type" to "admin"
        )
        db.collection("users").document(userId).collection("notifications").add(notif)
    } catch (_: Exception) {}
}

// Helper: add a minimal event document under users/{userId}/events
private fun addEventForUser(db: FirebaseFirestore, userId: String, titleFallback: String) {
    try {
        val eid = UUID.randomUUID().toString()
        val data = hashMapOf<String, Any?>(
            "id" to eid,
            "title" to "Evento desde Admin",
            "description" to "Evento agregado por administrador para $titleFallback",
            "date" to Timestamp.now(),
            "type" to EventType.EVENTO.name,
            "createdAt" to Timestamp.now(),
            "ownerId" to userId
        )
        db.collection("users").document(userId).collection("events").document(eid).set(data)
        // create notification for the user as well
        val notif = hashMapOf(
            "titulo" to "Nuevo evento",
            "cuerpo" to "Se agregó un evento a tu calendario",
            "remitente" to "admin",
            "senderName" to "Administrador",
            "fechaHora" to Timestamp.now(),
            "leida" to false,
            "relatedId" to eid,
            "type" to "event"
        )
        db.collection("users").document(userId).collection("notifications").add(notif)
    } catch (_: Exception) {}
}

