package com.example.appcolegios

import android.app.Application
import android.util.Log
import com.google.firebase.FirebaseApp
import com.google.firebase.database.FirebaseDatabase
import com.example.appcolegios.data.FirebaseRepository

class MyApplication : Application() {
    override fun onCreate() {
        super.onCreate()
        // Inicializar Firebase
        FirebaseApp.initializeApp(this)
        // Forzar el uso de la Realtime Database indicada
        try {
            val db = FirebaseDatabase.getInstance("https://appcolegios-a0c84-default-rtdb.firebaseio.com/")
            try {
                db.setPersistenceEnabled(true)
            } catch (_: Exception) {
                // Ignorar si ya fue habilitado en otro punto del ciclo de vida
            }
            // Ping de salud en DEBUG para verificar conectividad/escritura
            if (BuildConfig.DEBUG) {
                val repo = FirebaseRepository(db)
                val ts = System.currentTimeMillis()
                repo.set("app/health/pings/${ts}", mapOf("timestamp" to ts, "app" to "appcolegios")) { ok, e ->
                    if (!ok) Log.w("MyApplication", "RTDB health ping failed: ${e?.message}")
                }
            }
        } catch (_: Exception) {
            // Evitar crash en inicio si algo falla con Firebase
        }
    }
}

package com.example.appcolegios.auth

import android.content.Intent
import android.os.Bundle
import android.os.Handler
import android.os.Looper
import android.view.animation.AnimationUtils
import android.widget.ImageView
import androidx.appcompat.app.AppCompatActivity
import com.example.appcolegios.MainActivity
import com.example.appcolegios.R

class SplashActivity : AppCompatActivity() {

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_splash)

        val logoView: ImageView = findViewById(R.id.splashLogo)
        // Animación moderna: scale + fade
        val anim = AnimationUtils.loadAnimation(this, R.anim.splash_preload)
        logoView.startAnimation(anim)

        // Preload fijo de 3 segundos, luego seguir al flujo actual (MainActivity decide la startDestination)
        Handler(Looper.getMainLooper()).postDelayed({
            val next = Intent(this, MainActivity::class.java)
            // Preservar data (deep link) y extras si llegan
            intent?.data?.let { next.data = it }
            if (!intent.extras.isNullOrEmpty()) next.putExtras(intent.extras!!)
            startActivity(next)
            finish()
        }, 3000)
    }
}
